import React, { Component } from 'react';
class Table extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            isLoaded: true,
            error: null,
            data:[]
         }
    }
    componentDidMount(){
        fetch("https://api.wazirx.com/api/v2/tickers",{
            method:'GET',
        })
        .then(res => res.json())
        .then(
            (result) =>{
                this.setState({
                    data: result,
                    isLoaded: true
                });
                console.log("hello");
                console.log(result);
            },
            (error) =>{
                this.setState({
                    isLoaded:true,
                    error
                });
            }
        )
    }
    render() { 
        const {error, isLoaded, data} = this.state;
        if (error){
            return <div>Error: {error.message}</div>
        } else if(!isLoaded){
            return <div>Loading...</div>
        }
        else{
        return ( <div>
            <table>
                <tr>
                    <th>#</th>
                    <th>name</th>
                    <th>Last</th>
                    <th>Buy/Sell Price</th>
                    <th>volume</th>
                    <th>base_unit</th>
                </tr>
                
                {/* {data.map(finaldata =>(
                    <tr>
                        <td></td>
                        <td>{finaldata.name}</td>
                        <td>{finaldata.last}</td>
                        <td>{finaldata.buy}/{finaldata.sell}</td>
                        <td>{finaldata.volume}</td>
                        <td>{finaldata.base_unit}</td>
                    </tr>
                ))} */}
            </table>
        </div> );
        }
    }
}
 
export default Table;
