import React, { Component } from 'react';
class Table extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            isLoaded: true,
            error: null,
            data:[]
         }
    }
    componentDidMount(){
        fetch("https://api.wazirx.com/api/v2/tickers",{
            method:'GET',
            mode:'no-cors',
            headers:{
                'X-Requested-With': 'XMLHttpRequest'
            }
        })
        .then(res => res.json)
        .then(
            (result) =>{
                this.setState({
                    data: result,
                    isLoaded: true
                });
            },
            (error) =>{
                this.setState({
                    isLoaded:true,
                    error
                });
            }
        )
    }
    render() { 
        const {error, isLoaded, data} = this.state;
        if (error){
            return <div>Error: {error.message}</div>
        } else if(!isLoaded){
            return <div>Loading...</div>
        }
        else{
        return ( <div>
            <table>
                <thead>
                <tr>
                    <th>#</th>
                    <th>name</th>
                    <th>Last</th>
                    <th>Buy/Sell Price</th>
                    <th>volume</th>
                    <th>base_unit</th>
                </tr>
                </thead>
                <tbody>
                {data.btcinr.map(data =>(
                    <tr>
                        <td></td>
                        <td>{data.name}</td>
                        <td>{data.last}</td>
                        <td>{data.buy}/{data.sell}</td>
                        <td>{data.volume}</td>
                        <td>{data.base_unit}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div> );
        }
    }
}
 
export default Table;
